public class DirList {
    public static void main(String[] args) {
        String list[];
        File path = new File(".");
        if (args.length == 0) {
            list = path.list(); 
        } else {
            list = path.list(new DirFiler(args[0]));
        }
        // Arrayssortslist
        Arrays.sort(list, String.CASE_INSENSITIVE_ORDER);
        for (String file : list) {
            System.out.println(file);
        }
