// vue 2.x
module.exports = {
    dev: {
        assetsSubDirectory: 'static',
        assetsPublicPath: '/',
        // , [./dev.env.js]
        proxyTable: devEnv.OPEN_PROXY === false ? {} : {
            '/proxyApi': {
                target: 'http://192.168.50.37:8085/',
                changeOrigin: true,
                pathRewrite: {
                    '^/proxyApi': '/'
                }
            }
        },
        host: '192.168.50.39', // can be overwritten by process.env.HOST
        port: 8001
    }
}

// vue 3.x
devServer: {
    proxy: {
	    '/proxyApi': {
		    target: 'http://192.168.50.37:8085',
